import * as THREE from "three"
export class ns{
    constructor(scene){
        const texture = new THREE.TextureLoader().load('textures/smokeparticle.png')
        const geometry = new THREE.PlaneGeometry(400, 400);
        const vertexShader = `
            precision mediump float;
            varying vec2 vUv;
            void main() {
                vUv = uv;
                gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
            }
            `;
        const fragmentShader = `
            precision mediump float;
            uniform sampler2D uVelocity;
            uniform sampler2D uDensity;
            uniform float uDt;
            uniform float uViscosity;
            uniform sampler2D uSmokeTexture;
            varying vec2 vUv;

            const vec2 offsets[4] = vec2[](
                vec2(-1.0, 0.0),
                vec2(1.0, 0.0),
                vec2(0.0, -1.0),
                vec2(0.0, 1.0)
            );

            vec2 advect(sampler2D velocity, vec2 texCoord, float dt) {
                vec2 uv = texture2D(velocity, texCoord).xy;
                vec2 backtrace = texCoord - dt * uv;
                return texture2D(velocity, backtrace).xy;
            }

            vec2 diffuse(sampler2D velocity, vec2 texCoord, float viscosity, float dt) {
                vec2 uv = texture2D(velocity, texCoord).xy;
                vec2 laplacian = vec2(0.0);

                for (int i = 0; i < 4; i++) {
                    vec2 offset = offsets[i] / vec2(textureSize(velocity, 0));
                    laplacian += texture2D(velocity, texCoord + offset).xy;
                }

                laplacian -= 4.0 * uv;
                return uv + viscosity * dt * laplacian;
            }

            void main() {
                vec2 advectedVelocity = advect(uVelocity, vUv, uDt);
                vec2 diffusedVelocity = diffuse(uVelocity, vUv, uViscosity, uDt);
                vec4 smokeColor = texture2D(uSmokeTexture, vUv);
                float gray = (smokeColor.r + smokeColor.g + smokeColor.b) / 3.0;
                gl_FragColor = vec4(diffusedVelocity, smokeColor.b, 1.0);
            }
            `;
        this.rtWidth = window.innerWidth;
        this.rtHeight = window.innerHeight;
        this.renderTargets = [
            new THREE.WebGLRenderTarget(this.rtWidth, this.rtHeight),
            new THREE.WebGLRenderTarget(this.rtWidth, this.rtHeight)
        ];
        const initialField = this.initFields();
        // var mat = new THREE.MeshPhongMaterial({map:initialField})
        // var mesh = new THREE.Mesh(geometry, mat)
        // mesh.position.set(0,0,10)
        // scene.add(mesh)
        this.renderTargets[0].texture = initialField;
        this.renderTargets[1].texture = initialField.clone();
        
        this.smokeMaterial = new THREE.ShaderMaterial({
            vertexShader: vertexShader,
            fragmentShader: fragmentShader,
            uniforms: {
                uVelocity: { value: this.renderTargets[0].texture },
                uDensity: { value: null },
                uDt: { value: 1 / 60 },
                uViscosity: { value: 0.1 },
                uSmokeTexture: { value: texture },
            },
        });
        this.smokePlane = new THREE.Mesh(geometry, this.smokeMaterial);
        scene.add(this.smokePlane);
        this.currentFBO = 0
    }
    update(renderer, scene, camera){
        // const disturbanceSize = 10;
        // const halfSize = disturbanceSize / 2;
        // const gl = renderer.getContext();
        // const pixels = new Float32Array(disturbanceSize * disturbanceSize * 4);
        // const posX = Math.floor(Math.random() * this.rtWidth - halfSize);
        // const posY = Math.floor(Math.random() * this.rtHeight - halfSize);
      
        // gl.bindFramebuffer(gl.FRAMEBUFFER, this.renderTargets[this.currentFBO].glFramebuffer);
        // gl.readPixels(posX, posY, disturbanceSize, disturbanceSize, gl.RGBA, gl.FLOAT, pixels);
      
        // for (let j = 0; j < disturbanceSize; j++) {
        //     for (let i = 0; i < disturbanceSize; i++) {
        //         const index = (j * disturbanceSize + i) * 4;
        //         pixels[index] += (Math.random() - 0.5) * 0.01;
        //         pixels[index + 1] += (Math.random() - 0.5) * 0.01;
        //     }
        // }
      
        // gl.texSubImage2D(gl.TEXTURE_2D, 0, posX, posY, disturbanceSize, disturbanceSize, gl.RGBA, gl.FLOAT, pixels);
        // gl.bindFramebuffer(gl.FRAMEBUFFER, null);

        this.smokeMaterial.uniforms.uVelocity.value = this.renderTargets[this.currentFBO].texture;

        renderer.setRenderTarget(this.renderTargets[1 - this.currentFBO]);
        renderer.render(scene, camera);

        renderer.setRenderTarget(null);
        renderer.render(scene, camera);
        this.currentFBO = 1 - this.currentFBO;
    }

    initFields() {
        const data = new Float32Array(this.rtWidth * this.rtHeight * 4);
        for (let i = 0; i < this.rtWidth * this.rtHeight; i++) {
            data[i * 4] = Math.random() * 0.01 - 0.005;
            data[i * 4 + 1] = Math.random() * 0.01 - 0.005;
            data[i * 4 + 2] = 0;
            data[i * 4 + 3] = 1;
        }
        return new THREE.DataTexture(data, this.rtWidth, this.rtHeight, THREE.RGBAFormat, THREE.FloatType);
    }
      
    // addDisturbance(x, y, dx, dy) {

    // }
}


  
